DROP SCHEMA IF EXISTS vigo360_fuentes;
CREATE SCHEMA vigo360_fuentes;
USE vigo360_fuentes;

CREATE TABLE fuentes(
	id varchar(7) NOT NULL,
    titulo varchar(100) NOT NULL,
    ubicacion varchar(100) NOT NULL,
    parroquia varchar(20) NOT NULL,
    latitud double NOT NULL,
    longitud double NOT NULL,
    caudal enum("Bajo", "Medio", "Alto", "Ninguno", "Con llave de paso") NOT NULL,
    calidad enum("Sin calificar", "Mala", "Media", "Buena") NOT NULL,
	origen text NOT NULL,
    lavadero boolean NOT NULL,
    analizada smallint unsigned,
    nota text NOT NULL,
    PRIMARY KEY(id),
    INDEX(titulo),
    INDEX(caudal),
    INDEX(calidad),
    INDEX(parroquia),
    FULLTEXT(titulo, ubicacion, parroquia, origen, nota)
);

